widget.addContent( getContent() ); // ex. an HTMLPanel with content and actions.
widget.setGlassEnabled(true);
widget.setModal(true);
widget.setTitle("title");
widget.setSize(width, height);
widget.center();
widget.show();

widget.setOnCloseHandler(new ClickHandler() {
    @Override
    public void onClick(ClickEvent event) {
        someAction();
    }
});

MessageBox.showMessageBox("title", "message", MessageBox.MessageStyleType.WARN);
MessageBox.showMessageBox("title", "message", MessageBox.MessageStyleType.ERROR);
MessageBox.showMessageBox("title", "message", MessageBox.MessageStyleType.HELP);

MessageBox.showYesNoMessageBox("title", "message", new Callback<Boolean, Void>() {
    @Override
    public void onFailure(Void reason) {
        // Cancel or closed has been clicked.
    }

    @Override
    public void onSuccess(Boolean result) {
        // result is true when yes has been clicked, false for no.
    }
});
